/* Procedure structure for procedure `audit_member_pro` */

/*!50003 DROP PROCEDURE IF EXISTS  `audit_member_pro` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` PROCEDURE `audit_member_pro`(IN memberId VARCHAR(500),IN creater VARCHAR(500))
BEGIN
        DECLARE _memberId VARCHAR(50);
	DECLARE t_memberId VARCHAR(50);
	DECLARE num TINYINT DEFAULT 1;
	DECLARE i TINYINT DEFAULT 1;
	DECLARE s_num TINYINT DEFAULT 1;
	SET num = CEIL(LENGTH(memberId) / 34); 
	WHILE i <= num DO
		SET t_memberId = SUBSTRING(memberId,s_num,34);
		SET _memberId = SUBSTRING(t_memberId,1,32);
		 UPDATE  T_MEMBER_INFO a, T_MEMBER_INFO b
		       SET 
			a.AUDIT_STATUS = 2,
			a.UPDATE_DATE = b.UPDATE_DATE,
			a.MOBILE = b.MOBILE,
			a.LINKMAN = b.LINKMAN,
			a.MAIL = b.MAIL,
			a.COMPANY_NAME = b.COMPANY_NAME,
			a.COMPANY_URL = b.COMPANY_URL,
			a.OTHER_INFO = b.OTHER_INFO,
			a.COMPANY_CREATE_DATE = b.COMPANY_CREATE_DATE,
			a.COMPANY_CODE = b.COMPANY_CODE,
			a.COMPANY_ADDRESS = b.COMPANY_ADDRESS,
			a.MEMBER_TYPE = b.MEMBER_TYPE,
			a.AUDIT_INFO = b.AUDIT_INFO,
			a.creater = creater
		WHERE 
		   a.MEMBER_ID = _memberId AND b.MEMBER_ID  = t_memberId; 
		SET i = i + 1;
		SET s_num = s_num + 34;
		delete from T_MEMBER_INFO where MEMBER_ID = t_memberId;
	END WHILE;
	SELECT (i-1);
    END */$$
DELIMITER ;

/* Procedure structure for procedure `createProjectCopy_pro` */

/*!50003 DROP PROCEDURE IF EXISTS  `createProjectCopy_pro` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` PROCEDURE `createProjectCopy_pro`(IN projectId VARCHAR(50))
BEGIN
		#复制项目基本信息
		INSERT INTO t_project_info
		SELECT
			CONCAT(id,'_t')  AS id,
			MEMBER_ID,
			COMPANY_NAME,
			ESTABLISHED_DATE,
			ORGANIZING_CODE,
			COMPANY_ADDRESS,
			COMPANY_SUMMARY,
			PROJECT_NAME,
			PROJECT_TYPE,
			START_DATE,
			END_DATE,
			FIRST_DISCIPLINE,
			SECOND_DISCIPLINE,
			PROJECT_CONTACTS,
			CONTACTS_MOBILE_PHONE,
			CONTACTS_PHONE,
			CONTACTS_EMAIL,
			RESPONSIBLE_POSITION,
			RESPONSIBLE_POSITION_PHONE,
			HIGHEST_EDUCATION,
			PROFESSION,
			ID_NUMBER,
			UNITS_AND_POSITIONS,
			UNDERTAKE_TASKS,
			RESUME,
			SUMMARY,
			PROJECT_PHASE,
			IMPLEMENTATION_PLANS,
			PROJECT_TARGET,
			ECONOMIC_BENEFITS,
			-1 AS STATUS,
			SEQUENCE,
			CREATE_DATE,
			UPDATE_DATE,
			OPERATOR,
			RELEASE_DATE,
			UNDER_DATE,
			UNDER_REASON,
			REMARK,
			TOTAL_BUDGET,
			GOVERNMENT_SUBSIDY,
			OTHER_FINANCING,
			THIS_FINANCING,
			FINANCING_MATURITY,
			DEVELOPMENT_EXPENDITURE,
			FIXED_ASSETS_EXPENDITURE,
			MATERIALS_EXPENDITURE,
			PRODUCTION_COST,
			OPERATING_COSTS,
			OTHER_EXPENSES,
			TOTAL_EXPENSES
		FROM t_project_info
		WHERE id = projectId;
		#复制账务情况
		INSERT INTO t_company_finance
            (PROJECT_ID,
             YEAR,
             TOTAL_ASSETS,
             TOTAL_LIABILITY,
             TAKING,
             RETAINED_PROFITS,
             NET_CASH_FLOW)
		SELECT
			CONCAT(PROJECT_ID,'_t') AS PROJECT_ID,
			YEAR,
			TOTAL_ASSETS,
			TOTAL_LIABILITY,
			TAKING,
			RETAINED_PROFITS,
			NET_CASH_FLOW
		FROM t_company_finance
		WHERE PROJECT_ID = projectId;
		#复制计划进度
		INSERT INTO t_project_schedule_info
					(PROJECT_ID,
					 PHASE,
					 START_DATE,
					 END_DATE,
					 TARGET)
		SELECT
			CONCAT(PROJECT_ID,'_t') AS PROJECT_ID,
			PHASE,
			START_DATE,
			END_DATE,
			TARGET
		FROM t_project_schedule_info
		WHERE PROJECT_ID = projectId;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `send_member_pro` */

/*!50003 DROP PROCEDURE IF EXISTS  `send_member_pro` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` PROCEDURE `send_member_pro`(IN INID TINYINT)
BEGIN
	DECLARE sendeeid VARCHAR(330);
        SELECT SENDEE_ID INTO sendeeid  FROM t_notice_info WHERE ID = INID;
        IF LENGTH(sendeeid) > 0 THEN
        SET @sqlstr = CONCAT('select * from t_member_info where member_id',' IN(',sendeeid,')');  
        ELSE 
        SET @sqlstr =  CONCAT('select * from t_member_info where member_id = -1');      
        END IF;     
	PREPARE s1 FROM @sqlstr;  
	EXECUTE s1;  
	DEALLOCATE PREPARE s1;      
END */$$
DELIMITER ;

